targets.expr:
{% raw %}((1 - (1 - avg(rate(node_cpu_seconds_total{job="$job", mode="idle"}[$__rate_interval])) by (instance)) ^ 1.3) ^ (1/3) * 0.5 + 
( 1 - (1 - avg(node_memory_MemAvailable_bytes{job="$job"} / node_memory_MemTotal_bytes{job="$job"}) by (instance)) ^ 6) ^ (1/3) * 0.3 + 
(1 - max(rate(node_disk_io_time_seconds_total{job="$job"}[$__rate_interval]))by (instance) ^ 1.1) ^ (1/2) * 0.2) * 100{% endraw %}

{% raw %}(node_filesystem_size_bytes{job="$job", instance="$node", fstype=~"ext.*|xfs|nfs", mountpoint !~".*pod.*"} 
- node_filesystem_free_bytes{job="$job", instance="$node", fstype=~"ext.*|xfs|nfs", mountpoint !~".*pod.*"})
* 100 / node_filesystem_size_bytes{job="$job", instance="$node", fstype=~"ext.*|xfs|nfs", mountpoint !~".*pod.*"}{% endraw %}
